mapper.select:
select FROM_UNIXTIME(created,'%Y%m%d') as query_date,
       count(1) as count 
from IMUser 
where departId = #{param.organId,jdbcType=INTEGER}
  and FROM_UNIXTIME(created,'%Y%m%d') >= #{param.beginDate,jdbcType=VARCHAR}
  and FROM_UNIXTIME(created,'%Y%m%d') <= #{param.endDate,jdbcType=VARCHAR}
group by FROM_UNIXTIME(created,'%Y%m%d')
order by created desc;

select case sex when 1 then '男' when 0 then '女' else '未知' end as sex,
       count(1) as count 
from IMUser 
where departId = #{companyId,jdbcType=INTEGER}
group by sex;

select type,
       count(1) as count 
from IMUser 
where departId = #{companyId,jdbcType=INTEGER}
group by type;

select province,
       count(1) as count 
from IMUser 
where departId = #{companyId,jdbcType=INTEGER}
group by province;
